<?phpuse yii\helpers\Url;$url = \yii\helpers\Url::to('@web/img/map.jpg');if (!empty($sites)) {    $points = [];    foreach ($sites as $site) {        /** @var \common\models\Site $site */        $points[] = [            'id' => $site->id,            'title' => $site->name,            'x' => $site->x,            'y' => $site->y,        ];    }    $points = json_encode($points);    $baseUrl = Url::to(['site/view']);    $script = <<< JS            var points = $points;    var map = $('#wrap-map');    var baseUrl = '$baseUrl';        var img = document.querySelector('#wrap-map img')    function loaded() {                // map.css('max-width', map.children().width());                $.each(points, function(i, item) {            drawPoint(map, item.x, item.y, item.title, item.id);        });    }    if (img.complete) {        loaded()    } else {        img.addEventListener('load', loaded)        img.addEventListener('error', function() {            console.log('error')        })    }            function drawPoint(map, x, y, title, id) {        if (map && x && y && id) {            map.append($('<a class="point" data-toggle="tooltip" data-placement="right" data-html="true" title="' + title + '" href="' + baseUrl + '/' + id + '"></a>').css('left', map.width() * x).css('top', map.height() * y));            $('[data-toggle="tooltip"]').tooltip();                        return true;        }                   return false;    }        $(window).resize(function () {        $('.point').remove();        $.each(points, function(i, item) {            drawPoint(map, item.x, item.y, item.title, item.id);        });    });        $(".point").mouseover(function() {      $(this).css('z-index', 1);    });        $(".point").mouseout(function() {      $(this).css('z-index', 0);    });JS;$this->registerJs($script, yii\web\View::POS_READY);}?><h1><?= Yii::t('home', 'Sites') ?></h1></div><div class="relative">    <div id="wrap-map">        <img src="/img/map.jpg" alt="map" class="img-responsive" id="map">    </div></div><div class="container">